<template>
  <div class="content-container">
    <div class="text-label">Input n-th (digits):</div>
    <div class="input-class">
      <input type="text" v-model="digit" id="digit" />
      <button v-on:click="check()">Check</button>
    </div>
    <div v-if="result">
      <div>Result</div>
      <div>{{numberPosition}}th is {{result}}</div>
    </div>
  </div>
</template>

<script>
export default {
  name: "TaskTwo",
  data() {
    return {
      digit: "",
      numberPosition: "",
      result: 0
    };
  },
  methods: {
    check() {
      // As per my understanding number has to be entered from code level. 
      // There is no input box for that in pdf.
      let numberCheck =
        "";
      this.numberPosition = this.digit;
      this.result = numberCheck[this.digit];
    }
  }
};
</script>

